import React, { useState } from 'react';

const OpenQuestion = question =>
{
	return (
		<form className="ui form">
			<div className="field">
    			<label>{question}</label>
    			<input type="text" name="answer" placeholder="Answer here..."/>
  			</div>
		</form>
		);
}

export default OpenQuestion;
