import images from "../apis/images";
import { IMPORT_IMAGE } from "./types";

export const importImage = formValues =>
{
	return async dispatch => 
	{
		const response = await images.post('/images', formValues);

		dispatch({ type: IMPORT_IMAGE})
	};
};
